import React, { useEffect } from "react";

import Dropdown from "react-bootstrap/Dropdown";
import DropdownButton from "react-bootstrap/DropdownButton";
import { useDispatch, useSelector } from "react-redux";

import { Container, Nav, Navbar, NavDropdown } from "react-bootstrap";
import { Route, Lin, BrowserRouter, Routes } from "react-router-dom";
import { LinkContainer } from "react-router-bootstrap";
import { logout } from "../actions/userActions";
import SearchBox from "../components/SearchBox";
import { Link } from "react-router-dom";
const Header = () => {
  // const navigate=useNavigate()
  const dispath = useDispatch();
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  useEffect(() => {
    if (userInfo) {
      // window.location.reload();
    }
  }, [userInfo]);
  const logoutHandler = () => {
    dispath(logout());
    window.location.reload();
  };
  return (
    <header>
      <Navbar bg="dark" variant="dark" expand="lg" collapseOnSelect>
        <Container>
          <Navbar.Brand href="/">proshop</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <BrowserRouter>
              <Routes>
                {/* <SearchBox/> */}
                <Route path="/search/:keyword" element={<SearchBox />} />
                {/* <SearchBox/> */}
              </Routes>
            </BrowserRouter>
            <Nav className="mr-auto">
              <Nav.Link href="/cart">
                <i className="fas fa-shopping-cart"></i>Cart
              </Nav.Link>
              {userInfo ? (
                <DropdownButton
                  id="dropdown-basic-button"
                  title={userInfo.name}
                >
                  <Dropdown.Item href="/profile">Profile</Dropdown.Item>
                  <Dropdown.Item onClick={logoutHandler}>Logout</Dropdown.Item>
                </DropdownButton>
              ) : (
                // <NavDropdown title={userInfo.name}id='username'>
                //   <LinkContainer to='/profile'>
                //     <NavDropdown title="profile">
                //       <NavDropdown.Item>Profile</NavDropdown.Item>
                //     </NavDropdown>
                //     <LinkContainer to='/' onClick={logoutHandler}>
                //       <NavDropdown.Item>Logout</NavDropdown.Item>
                //     </LinkContainer>
                //   </LinkContainer>

                // </NavDropdown>

                // <a href='/login'>
                <Nav.Link href="/login">
                  <i className="fas fa-user"></i>Sigin In
                </Nav.Link>
              )}

              {userInfo && userInfo.name === "Admin User" && (
                <DropdownButton id="adminmenu" title="Admin">
                  <Dropdown.Item href="/admin/userlist">Users</Dropdown.Item>
                  <Dropdown.Item href="/admin/productlist">
                    Product
                  </Dropdown.Item>
                  <Dropdown.Item href="/admin/orderlist">Orders</Dropdown.Item>
                </DropdownButton>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </header>
  );
};

export default Header;
